package com.hiveview.tv.service.dao;import com.hiveview.tv.service.exception.ErrorCode;import android.content.Context;import android.content.SharedPreferences;public class CommonPreferenceDAO {		private final String PREFERENCE = "DomyBoxPreference";	private final String IS_SHOW_GUIDE = "IS_SHOW_GUIDE";		private SharedPreferences.Editor editor;	private SharedPreferences preference;		@SuppressWarnings("unused")	private CommonPreferenceDAO(){};		public CommonPreferenceDAO(Context context) {			preference = context.getSharedPreferences(PREFERENCE, Context.MODE_PRIVATE);		editor = preference.edit();			}		public void setIsShowGudie(boolean isShowGuide){		editor.putBoolean(IS_SHOW_GUIDE, isShowGuide);		editor.commit();	}		public boolean getIsShowGudie(){		return preference.getBoolean(IS_SHOW_GUIDE, false);	}		public void clearErrorCode(){		editor.remove(IS_SHOW_GUIDE);		editor.commit();	}}