package com.hiveview.tv.service.parser;import java.io.InputStream;import java.util.ArrayList;import org.json.JSONException;import org.json.JSONObject;import com.hiveview.box.framework.entity.HiveBaseEntity;import com.hiveview.tv.service.exception.ErrorCode;import com.hiveview.tv.service.exception.ServiceException;import com.hiveview.tv.utils.HiveViewErrorCode;import com.hiveview.tv.utils.StringUtils;public class CommonParser extends BaseParser {	@Override	public ArrayList<? extends HiveBaseEntity> executeToObject(InputStream in) throws ServiceException {		String jsonStr = StringUtils.converStreamToString(in,StringUtils.GB2312);		try {			JSONObject obj = new JSONObject(jsonStr);			errorCode = obj.getString("code");		} catch (JSONException e) {			throw new ServiceException(HiveViewErrorCode.E0000598);		}		return null;	}	@Override	public String getErrorCode() {		return errorCode;	}	public String getDateString() {		return date;	}}