package com.hiveview.tv.service.request;import java.io.UnsupportedEncodingException;import java.util.ArrayList;import java.util.List;import org.apache.http.NameValuePair;import org.apache.http.client.entity.UrlEncodedFormEntity;import org.apache.http.client.methods.HttpPost;import org.apache.http.message.BasicNameValuePair;import com.hiveview.tv.common.AppConstant;import com.hiveview.tv.service.exception.ServiceException;public class DevCheckRequest extends BasePostRequest {	private String deviceid;	private String mac;	private String uuid;	private String md5key;	@SuppressWarnings("unused")	private DevCheckRequest() {	}	public DevCheckRequest(String version, String deviceid, String mac, String uuid) {		this.version = version;		this.deviceid = deviceid;		this.mac = mac;		this.uuid = uuid;	};	@Override	public HttpPost executeToEntity() throws UnsupportedEncodingException {		HttpPost httpPost = new HttpPost(String.format(ApiConstant.HIVEVIEW_TV_API_DEVICE_CHECK));		List<NameValuePair> paramList = new ArrayList<NameValuePair>(AppConstant.NO_5);		paramList.add(new BasicNameValuePair("mac", "14-3D-F2-01-00-00"));		paramList.add(new BasicNameValuePair("sn", "85c2383bebf7226a0317cdeb061c78e2"));		paramList.add(new BasicNameValuePair("time", "1393398671563"));		paramList.add(new BasicNameValuePair("md5key", "bd2955bdff861b1e5e66a486c29d8ce7"));		paramList.add(new BasicNameValuePair("version", ApiConstant.APP_VERSION));		httpPost.setEntity(new UrlEncodedFormEntity(paramList, "UTF-8"));		return httpPost;	}	@Override	public String executeUrl() throws ServiceException {		return String.format(ApiConstant.HIVEVIEW_TV_API_DEVICE_CHECK);	}}